INSERT INTO `people_declarations` (`id`, `idperson`, `source`, `time`, `declaration`) VALUES
(127, 337, 'http://www.cdep.ro/pls/steno/steno.stenograma?ids=13#6', 888210000, '<p align="justify">Domnule preşedinte, <p align="justify">Stimaţi colegi, <p align="justify">În câteva propoziţii. La 26 iunie anul acesta se împlinesc 150 de ani de la declararea oficială a tricolorului românesc ca drapel naţional. <p align="justify">Aş vrea să încercăm cu toţii să facem în aşa fel încât, foarte rapid, să putem să stabilim ziua de 26 iunie ca zi a drapelului naţional. <p align="justify">Proiectul de lege este foarte restrâns, el are un caracter mai curând de semnificaţie istorică şi aş vrea, în încheiere, să vă citez cuvintele lui Cuza care spunea în 1863: "Drapelul este România, acest pământ binecuvântat al patriei udat cu sângele străbunilor noştri şi îmbogăţit cu sudoarea muncitorului. El este familia, ogorul fiecăruia, casa în care s-au născut părinţii şi unde se vor naşte copiii noştri. Steagul este totodată trecutul, prezentul şi viitorul ţării, întreaga istorie a României". <p align="justify">Vă cer dumneavoastră, stimaţi colegi, să fiţi alături de noi în acest demers care are o semnificaţie de istorie şi în acelaşi timp o semnificaţie pentru momentele pe care le trăim, astfel încât să putem la 26 iunie să avem această lege promulgată. De aceea procedura de urgenţă este absolut necesară. <p align="justify">Vă mulţumesc. ');
INSERT INTO `people_declarations_highlights` (`id`, `source`, `user_id`, `start_word`, `end_word`, `content`) VALUES
(6, 'http://www.cdep.ro/pls/steno/steno.stenograma?ids=13#6', 1, 4, 26, 'În câteva propoziţii. La 26 iunie anul acesta se împlinesc 150 de ani de la declararea oficială a tricolorului românesc ca drapel naţional.'),
(8, 'http://www.cdep.ro/pls/steno/steno.stenograma?ids=5#11', 1, 2, 18, 'Stimaţi colegi, Este, până la urmă, o chestiune de principiu aceea pe care o vom decide aici.'),
(9, 'http://www.cdep.ro/pls/steno/steno.stenograma?ids=5#11', 1, 38, 103, 'trebuie să alegem între a recunoaşte rolul Parlamentului în chestiuni care sunt extrem de importante şi care ţin, ca finalitate, de modelarea economică viitoare a României, şi între viziunea Guvernului, care doreşte să meargă cu aspectul executiv până la chestiuni care, în cele din urmă, vor stabili atât de puternic configuraţia economică a ţării, încât ea nu va mai putea fi vreodată modificată în alt mod.');
